package Selenium;

public class FizzBuzz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		       int a=12;
		     if(a%5==0 && a%3==0){
		         System.out.println("FizzBuzz is there");
		     }
		     else if(a%5==0){
		         System.out.println("Fizz");
		     }
		     else if(a%3==0){
		         System.out.println("Buzz");
		     }
		     else{
		         System.out.println(a);
		     }
		   
	}

}
